// src/components/FormularioReserva.jsx
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const FormularioReserva = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    nombre: "",
    correo: "",
    telefono: "",
    entrada: "",
    salida: "",
    habitacion: "Estandar",
    cantidad: 1,
    ocupantes: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    navigate("/confirmacion", { state: formData });
  };

  return (
    <div className="min-h-screen bg-white flex justify-center p-8">
      <div className="w-full max-w-2xl space-y-6">
        <h1 className="text-2xl font-bold text-gray-800">Formulario de Reserva</h1>

        <form className="space-y-4 bg-gray-100 p-6 rounded-lg shadow" onSubmit={handleSubmit}>
          <input className="w-full p-2 border rounded" name="nombre" placeholder="Nombre completo" onChange={handleChange} required />
          <input className="w-full p-2 border rounded" name="correo" type="email" placeholder="Correo electrónico" onChange={handleChange} required />
          <input className="w-full p-2 border rounded" name="telefono" placeholder="Teléfono" onChange={handleChange} required />
          <input className="w-full p-2 border rounded" name="entrada" type="datetime-local" onChange={handleChange} required />
          <input className="w-full p-2 border rounded" name="salida" type="datetime-local" onChange={handleChange} required />

          <div className="space-y-2">
            <p className="font-medium">Tipo de habitación:</p>
            {["Estandar", "Suite", "De lujo"].map((tipo) => (
              <label key={tipo} className="block">
                <input
                  type="radio"
                  name="habitacion"
                  value={tipo}
                  checked={formData.habitacion === tipo}
                  onChange={handleChange}
                  className="mr-2"
                />
                {tipo}
              </label>
            ))}
          </div>

          <input
            className="w-full p-2 border rounded"
            name="cantidad"
            type="number"
            placeholder="Cantidad de habitaciones"
            min={1}
            max={20}
            onChange={handleChange}
            required
          />
          <input
            className="w-full p-2 border rounded"
            name="ocupantes"
            placeholder="Número de ocupantes (ej. 2 adultos, 1 niño)"
            onChange={handleChange}
            required
          />

          <button type="submit" className="w-full bg-blue-600 text-white font-bold p-2 rounded hover:bg-blue-700">
            Reservar
          </button>
        </form>
      </div>
    </div>
  );
};

export default FormularioReserva;
