import React from "react";

const ConfirmacionReserva = ({ datos }) => {
  const handleNueva = () => {
    window.location.reload(); // Simula reiniciar
  };

  return (
    <div className="space-y-4 text-center">
      <h2 className="text-3xl font-bold text-green-600">¡Reserva Confirmada!</h2>
      <p className="text-lg">Gracias, <strong>{datos.nombre}</strong></p>
      <div className="grid gap-2 text-gray-700">
        <p><strong>Email:</strong> {datos.email}</p>
        <p><strong>Teléfono:</strong> {datos.telefono}</p>
        <p><strong>Entrada:</strong> {datos.entrada}</p>
        <p><strong>Salida:</strong> {datos.salida}</p>
        <p><strong>Personas:</strong> {datos.personas}</p>
      </div>
      <button
        onClick={handleNueva}
        className="mt-4 bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700 transition"
      >
        Hacer otra reserva
      </button>
    </div>
  );
};

export default ConfirmacionReserva;
