import React from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { FaCheckCircle, FaArrowLeft } from "react-icons/fa";

const ConfirmacionReserva = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const datos = location.state;

  if (!datos) {
    return (
      <div className="h-screen flex items-center justify-center">
        <p className="text-lg text-red-600">No hay datos de la reserva.</p>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-green-100 to-white flex items-center justify-center px-4">
      <div className="max-w-2xl w-full bg-white p-8 shadow-2xl rounded-2xl">
        <div className="flex items-center justify-center mb-6">
          <FaCheckCircle className="text-green-500 text-4xl mr-2" />
          <h1 className="text-2xl font-bold text-green-700">¡Reserva Confirmada!</h1>
        </div>

        <div className="space-y-4 text-gray-800">
          <p><strong>Nombre:</strong> {datos.nombre}</p>
          <p><strong>Correo:</strong> {datos.correo}</p>
          <p><strong>Teléfono:</strong> {datos.telefono}</p>
          <p><strong>Entrada:</strong> {datos.entrada}</p>
          <p><strong>Salida:</strong> {datos.salida}</p>
          <p><strong>Tipo de habitación:</strong> {datos.habitacion}</p>
          <p><strong>Cantidad:</strong> {datos.cantidad}</p>
          <p><strong>Ocupantes:</strong> {datos.ocupantes}</p>
        </div>

        <div className="mt-6 text-center">
          <button
            onClick={() => navigate("/")}
            className="inline-flex items-center gap-2 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition"
          >
            <FaArrowLeft /> Volver al inicio
          </button>
        </div>
      </div>
    </div>
  );
};

export default ConfirmacionReserva;
