import React from "react";

const Header = () => {
  return (
    <header className="bg-white shadow-md py-4 px-8 flex justify-between items-center">
      <h1 className="text-2xl font-bold text-blue-700">Hotel Paraíso</h1>
      {/* Puedes poner un logo aquí si tienes uno */}
      <img src="/logo.png" alt="Logo del Hotel" className="h-10" />
    </header>
  );
};

export default Header;
