import React from "react";
import { useLocation, useNavigate } from "react-router-dom";
import Header from "../components/Header";
import {
  FaCheckCircle,
  FaCalendarAlt,
  FaBed,
  FaUser,
  FaPhone,
  FaEnvelope,
} from "react-icons/fa";
import successGif from "../assets/check.gif"; // Asegúrate de tener una animación en assets

const ConfirmacionReserva = () => {
  const { state } = useLocation();
  const navigate = useNavigate();

  if (!state) {
    return (
      <div className="min-h-screen flex justify-center items-center text-xl text-red-500">
        No hay datos de la reserva.
      </div>
    );
  }

  const {
    nombre,
    correo,
    telefono,
    entrada,
    salida,
    habitacion,
    cantidad,
    ocupantes,
  } = state;

  return (
    <div className="min-h-screen bg-gradient-to-br from-green-100 to-white px-4 py-6">
      <Header />

      <div className="max-w-5xl mx-auto bg-white/90 backdrop-blur-lg shadow-xl rounded-2xl p-6 mt-10 flex flex-col md:flex-row gap-6">
        {/* Parte Izquierda: Detalles */}
        <div className="md:w-2/3">
          <div className="flex items-center gap-3 mb-6">
            <FaCheckCircle className="text-green-600 text-3xl animate-pulse" />
            <h2 className="text-2xl font-bold text-green-800">¡Reserva Confirmada!</h2>
          </div>

          <p className="mb-6 text-gray-600">
            Gracias <strong>{nombre}</strong>, tu reserva ha sido registrada con éxito. Aquí están los detalles:
          </p>

          <div className="space-y-4 text-gray-700">
            <div className="flex items-center gap-3">
              <FaUser className="text-blue-500" />
              <span className="font-medium">Nombre:</span> {nombre}
            </div>
            <div className="flex items-center gap-3">
              <FaEnvelope className="text-blue-500" />
              <span className="font-medium">Correo:</span> {correo}
            </div>
            <div className="flex items-center gap-3">
              <FaPhone className="text-blue-500" />
              <span className="font-medium">Teléfono:</span> {telefono}
            </div>
            <div className="flex items-center gap-3">
              <FaCalendarAlt className="text-blue-500" />
              <span className="font-medium">Entrada:</span> {new Date(entrada).toLocaleString()}
            </div>
            <div className="flex items-center gap-3">
              <FaCalendarAlt className="text-blue-500" />
              <span className="font-medium">Salida:</span> {new Date(salida).toLocaleString()}
            </div>
            <div className="flex items-center gap-3">
              <FaBed className="text-blue-500" />
              <span className="font-medium">Habitación:</span> {habitacion} ({cantidad})
            </div>
            <div className="flex items-center gap-3">
              <FaUser className="text-blue-500" />
              <span className="font-medium">Ocupantes:</span> {ocupantes}
            </div>
          </div>

          <button
            onClick={() => navigate("/")}
            className="mt-8 w-full bg-blue-600 text-white font-bold py-3 rounded hover:bg-blue-700 transition"
          >
            Volver al inicio
          </button>
        </div>

        {/* Parte Derecha: Imagen decorativa o animación */}
        <div className="md:w-1/3 flex items-center justify-center">
          <img
            src={successGif} // Puede ser check.gif o una ilustración
            alt="Confirmación"
            className="w-64 h-auto"
          />
        </div>
      </div>
    </div>
  );
};

export default ConfirmacionReserva;
