// src/components/ConfirmacionReserva.jsx
import React from "react";
import { useLocation, useNavigate } from "react-router-dom";

const ConfirmacionReserva = () => {
  const { state } = useLocation();
  const navigate = useNavigate();

  if (!state) return <div className="p-4">No hay datos de reserva.</div>;

  return (
    <div className="min-h-screen bg-white flex justify-center p-8">
      <div className="max-w-xl w-full bg-green-100 p-6 rounded shadow space-y-4">
        <h2 className="text-xl font-bold text-green-700">¡Reserva confirmada!</h2>
        <p><strong>Nombre:</strong> {state.nombre}</p>
        <p><strong>Correo:</strong> {state.correo}</p>
        <p><strong>Teléfono:</strong> {state.telefono}</p>
        <p><strong>Entrada:</strong> {state.entrada}</p>
        <p><strong>Salida:</strong> {state.salida}</p>
        <p><strong>Habitación:</strong> {state.habitacion}</p>
        <p><strong>Cantidad:</strong> {state.cantidad}</p>
        <p><strong>Ocupantes:</strong> {state.ocupantes}</p>

        <button onClick={() => navigate("/")} className="bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-600">
          Hacer otra reserva
        </button>
      </div>
    </div>
  );
};

export default ConfirmacionReserva;
